var ppp=6,pageNumber=1;function load_posts(){var e="&pageNumber="+ ++pageNumber+"&ppp="+ppp+"&action=more_post_ajax";return jQuery.ajax({type:"POST",dataType:"html",cache:!1,url:ajax_posts.ajaxurl,data:e,success:function(e){var a=jQuery(e);a.length?(jQuery("#ajax-posts").append(a),jQuery(".no-posts-message").hide()):jQuery(".no-posts-message").show().delay(2e3).fadeOut()},error:function(e,a,o){console.log(o)}}),!1}jQuery("#more_posts").on("click",(function(e){e.preventDefault(),load_posts()}));